using System.Collections.Generic;
using System.IO;
using System.Reflection;
using AutopilotMonitor.Shared.Models;
using Newtonsoft.Json;

namespace AutopilotMonitor.Functions.Services
{
    /// <summary>
    /// Built-in gather rules loaded from embedded JSON resource.
    /// The JSON is generated by the CI combine script from individual rule files in rules/gather/.
    /// </summary>
    public static class BuiltInGatherRules
    {
        public static List<GatherRule> GetAll()
        {
            var assembly = Assembly.GetExecutingAssembly();
            using var stream = assembly.GetManifestResourceStream(
                "AutopilotMonitor.Functions.Rules.gather-rules.json");
            using var reader = new StreamReader(stream!);
            var json = reader.ReadToEnd();
            var wrapper = JsonConvert.DeserializeObject<RulesWrapper<GatherRule>>(json);
            var rules = wrapper?.Rules ?? new List<GatherRule>();

            foreach (var rule in rules)
            {
                rule.IsBuiltIn = true;
            }

            return rules;
        }
    }
}
